PS C:\> $arn1 = "arn:aws:s3:::mybucket"
PS C:\> $arn2 = "arn:aws:dynamodb:us-west-2:123456789012:table/mytable"

PS C:\> Add-RGTResourceTag -ResourceARNList $arn1,$arn2 -Tag @{ "stage"="beta"; "version"="preprod_test" }

