PS C:\>$rule1 = New-Object Amazon.S3.Model.ReplicationRule PS C:\>$rule1.ID = "Rule-1" PS C:\>$rule1.Status = "Enabled" PS C:\>$rule1.Prefix = "TaxDocs" PS C:\>$rule1.Destination = @{ BucketArn = "arn:aws:s3:::exampletargetbucket" }
PS C:\>$params = @{ BucketName = "examplebucket" Configuration_Role = "arn:aws:iam::35667example:role/CrossRegionReplicationRoleForS3" Configuration_Rule = $rule1 }
PS C:\>Write-S3BucketReplication @params

