PS C:\>$rule2 = New-Object Amazon.S3.Model.ReplicationRule PS C:\>$rule2.ID = "Rule-2" PS C:\>$rule2.Status = "Enabled" PS C:\>$rule2.Prefix = "OtherDocs" PS C:\>$rule2.Destination = @{ BucketArn = "arn:aws:s3:::exampletargetbucket" }
